function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".header__links"),t=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),s.addEventListener("click",(c=>{c.target&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}burgerMenu();